import React, {Component} from 'react'

class Form extends Component {

  cities = [
    'Stockholm',
    'Paris',
    'Tokyo',
    'Bangkok',
    'Bandung'
  ];

  constructor(props) {
    super(props);
    this.state = {
      values : 'Stockholm',
      errors:''
    };
  }

  componentDidMount(){
    this.sddsDropdown.addEventListener('selectOption', (evt) => {
      const newValue = evt.detail.label;
      this.setState({...this.state, values:newValue})

      // Input validation
      const errorMessage = this.cityValidation(newValue);
      this.setState({...this.state, errors: errorMessage})
    })
  }

  cityValidation(selected){
    if(selected.trim() === '') return 'City is required';
    if(selected==='Tokyo') return 'Sorry, you cannot live in Tokyo';
    return null;
  }

  render() {
    return (

        <div className="container">
          <sdds-dropdown
            name="city"
            placeholder="Select city"
            label="City"
            label-position="outside"
            default-option={this.state.values}
            ref={elem => this.sddsDropdown = elem}
            state={this.state.errors && 'error'}
            helper={this.state.errors || 'Please select a city' }
            >
            {this.cities.map((city) => <sdds-dropdown-option key={"id-" + city} value={city}>{city}</sdds-dropdown-option>)}
          </sdds-dropdown>
          <p>Selected value:</p>
          <p>{this.state.values}</p>
        </div>
    );
  }
}

export default Form;

