import { Component, forwardRef, Input } from '@angular/core';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';

// https://angular.io/api/forms/ControlValueAccessor

@Component({
  selector:'custom-dropdown',
  template: `
  <sdds-dropdown
  label="Label text"
  label-position="outside"
  placeholder="Select option"
  placeholder="Select option"
  [defaultOption]="initialValue"
  [state]="myValue.label==='' || myValue.label==='None' ? 'error' : ''"
  (selectOption)="handleChange($event)">
    <sdds-dropdown-option value="">None</sdds-dropdown-option>
    <sdds-dropdown-option 
      *ngFor="let option of list" 
      [selected]="option.value === initialValue.value"
      [value]="option.value">
        {{option.label}}
    </sdds-dropdown-option>
  </sdds-dropdown>
`,
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => CustomDropdown),
      multi: true
    }
  ]
})

export class CustomDropdown implements ControlValueAccessor {
  @Input() list:any;
  @Input() initialValue:any;
  @Input() myValue:any;

  value: string;
  onChange: (value: string) => void;
  onTouched: () => {};
  onDisabled: boolean;

  constructor() {
    this.onChange = (value:string) => {};
  }

  writeValue(value: string) {
    this.value = value ? value : '';
  }

  registerOnChange(onChange: any)  {
    this.onChange = onChange;
  }

  registerOnTouched(fn: any)  {
    this.onTouched = fn;
  }

  setDisabledState?(isDisabled: boolean)  {
    this.onDisabled = isDisabled;
  }

  handleChange(event) {
    console.log(event)
    this.onChange(event.detail.label)
  }

}

